mapper.update:
UPDATE book
SET book_title=
#{book_title}, book_picture_url=#{book_picture_url},
book_is_public=#{book_is_public}
WHERE book_no=#{book_no}

UPDATE book
SET bookmark='true'
WHERE book_no=#{value}

UPDATE book
SET bookmark='false'
WHERE book_no=#{value}

mapper.select:
SELECT DISTINCT w.email
FROM `write` w
WHERE w.book_no=#{value}
